local MainTable = { }local bug = {}local myBitch = {}blips = {}local localTexts = {{ "Don't shoot me, just get what you need!" },{ "Please dont kill me i have kids" },{ "I'm begging you leave me alone" },{ "Calm down dude!!" },{ "Take the money and leave!!" },{ "No problem take the cash and leave" },{ "I won't call cops just leave!" },}addEvent("syncPeds", true)function setPeds ( peds )	for i, v in ipairs ( peds ) do		addEventHandler("onClientPedDamage", v, function () cancelEvent() end )	endendaddEventHandler("syncPeds", root, setPeds)addEventHandler( "onClientProjectileCreation", root,function ( creator )	if ( getElementData ( localPlayer, "isPlayerRobbingStore" ) ) then		if ( getProjectileType( source ) == 16 ) or ( getProjectileType( source ) == 17 ) or ( getProjectileType( source ) == 18 ) or ( getProjectileType( source ) == 39 ) then			if ( creator == localPlayer ) then				exports.NGCdxmsg:createNewDxMessage("You can't use explosions while you are robbing store",255,0,0)			end			destroyElement( source )		end	endend)addEventHandler("onClientExplosion", root,function(x, y, z, theType)	if (getElementData(localPlayer, "isPlayerRobbingStore")) then		cancelEvent()	endend)addEvent("synceStoresBlips",true)addEventHandler("synceStoresBlips",root,function(x,y,v,types)	if types == "robbed" then		if blips[v] then			exports.customblips:destroyCustomBlip(blips[v])			blips[v] = nil		end		if blips[v] == nil or blips[v] == false then			blips[v] = exports.customblips:createCustomBlip( x,y,18,18,"robbed.png",1000)		end	else		if blips[v] then			exports.customblips:destroyCustomBlip(blips[v])			blips[v] = nil		end		if blips[v] == nil or blips[v] == false then			blips[v] = exports.customblips:createCustomBlip( x,y,18,18,"norobbed.png",1000)		end	endend)addEvent("addServerMsg", true)function msg(player,msg)	if player == localPlayer then		exports.NGCdxmsg:createNewDxMessage( msg,255,255,0)	endendaddEventHandler("addServerMsg", root, msg)addEvent("checkForPlayerAim", true)function checkAim ( actionType, ped )	if ( source ~= localPlayer ) then return end	if exports.DENlaw:isLaw(localPlayer) then return end	if not ( isPedAiming ( source ) ) then return end	if ( actionType == "newRob" ) then		triggerServerEvent( "startStoreRob", localPlayer, ped )	elseif ( actionType == "joinRob" ) then		triggerServerEvent( "addPlayerToStoreRob", localPlayer, ped )	endendaddEventHandler("checkForPlayerAim", root, checkAim)addEvent("startStoreRob", true)function startStoreRob ( player, ped )	-- Tell the player he started a store rob	myBitch = ped	if ( localPlayer == player ) then		exports.NGCdxmsg:createNewDxMessage( "Watch out police found you, hurry up",255,255,0)	end	-- Warn the police -- law check	if exports.DENlaw:isLaw(localPlayer) then		local zone = getElementData(player,"shopName") or "Unknown"		exports.killMessages:outputMessage( "(Robbery:["..getPlayerName(player).."]) is robbing a store in ["..zone.."] Please respond!" , 0, 100, 200,"default-bold" )	end	MainTable [#MainTable +1 ] = { ped, math.random ( #localTexts ) }	setHandsUpAnimation ( ped )	addEventHandler( "onClientElementStreamIn", ped, setHandsUpAnimation )	setTimer ( function () setCoverAnimation(ped) removeEventHandler( "onClientElementStreamIn", ped, setHandsUpAnimation )	addEventHandler( "onClientElementStreamIn", ped, setCoverAnimation ) end, 60000, 1)	setTimer( function () removeEventHandler( "onClientElementStreamIn", ped, setCoverAnimation ) setPedAnimation ( ped ) MainTable[#MainTable] = nil end, 90000, 1)	--[[bug[player] = setTimer(function(player,ped)		if player == localPlayer then			if getElementData(localPlayer,"isPlayerArrested") or getElementData(localPlayer,"isPlayerJailed") then				triggerServerEvent("abortStoreRobbery",player,ped)				setPedAnimation ( ped )				MainTable[#MainTable] = nil			end		end	end,5000,0,player,ped)]]endaddEventHandler("startStoreRob", root, startStoreRob)addEventHandler("onClientPlayerQuit",localPlayer,function()	if myBitch then		if getElementType(myBitch) == "ped" then			triggerServerEvent("abortStoreRobbery",localPlayer,myBitch)			setPedAnimation ( myBitch )			MainTable[#MainTable] = nil		end	endend)addEvent("endTheRobbery",true)addEventHandler("endTheRobbery",root,function(gay)	--[[if isTimer(bug[gay]) then		killTimer(bug[gay])	end	setPedAnimation ( myBitch )	MainTable[#MainTable] = nil]]end)addEvent("addPlayerToStoreRob", true)function addPlayerToStoreRob ( player, ped )	if ( player == localPlayer ) then		exports.NGCdxmsg:createNewDxMessage( "You have joined the robbery!",255,255,0)		exports.NGCdxmsg:createNewDxMessage( "Watch out police found you, hurry up",255,255,0)	endendaddEventHandler("addPlayerToStoreRob", root, addPlayerToStoreRob)function setHandsUpAnimation ( ped )	if ( ped ) then		setPedAnimation( ped, "ped", "handsup", -1, false, true, false )	else		setPedAnimation( source, "ped", "handsup", -1, false, true, false )	endendfunction setCoverAnimation ( ped )	if ( ped ) then		setPedAnimation( ped, "ped", "cower", -1, false, false, false )	else		setPedAnimation( source, "ped", "cower", -1, false, false, false )	endendfunction drawTextBubble ()	for i=1, #MainTable do		drawText (MainTable[i][1], localTexts[MainTable[i][2]][1] )	endendaddEventHandler( "onClientRender", root,  drawTextBubble  )function drawText (ped, text) --- local chat for ped	if ( isElement( ped ) ) then		local camPosXl, camPosYl, camPosZl = getPedBonePosition (ped, 6)		local camPosXr, camPosYr, camPosZr = getPedBonePosition (ped, 7)		local x,y,z = (camPosXl + camPosXr) / 2, (camPosYl + camPosYr) / 2, (camPosZl + camPosZr) / 2		local cx,cy,cz = getCameraMatrix()		local px,py,pz = getElementPosition(ped)		local distance = getDistanceBetweenPoints3D(cx,cy,cz,px,py,pz)		local posx,posy = getScreenFromWorldPosition(x,y,z+0.020*distance+0.10)		local elementtoignore1 = getPedOccupiedVehicle(localPlayer) or localPlayer		local elementtoignore2 = getPedOccupiedVehicle(ped) or ped		if posx and distance <= 10 then --and ( isLineOfSightClear(cx,cy,cz,px,py,pz,true,true,false,true,false,true,true,elementtoignore1) or isLineOfSightClear(cx,cy,cz,px,py,pz,true,true,false,true,false,true,true,elementtoignore2) ) then -- change this when multiple ignored elements can be specified			local width = dxGetTextWidth(text,1,"default-bold")			dxDrawRectangle(posx - (3 + (0.5 * width)),posy - (2 + (0 * 20)),width + 5,19,tocolor(0,0,0,180))			dxDrawRectangle(posx - (6 + (0.5 * width)),posy - (2 + (0 * 20)),width + 11,19,tocolor(0,0,0,0))			dxDrawRectangle(posx - (8 + (0.5 * width)),posy - (1 + (0 * 20)),width + 15,17,tocolor(0,0,0,180))			dxDrawRectangle(posx - (10 + (0.5 * width)),posy - (1 + (0 * 20)),width + 19,17,tocolor(0,0,0,0))			dxDrawRectangle(posx - (10 + (0.5 * width)),posy - (0 * 20) + 1,width + 19,13,tocolor(0,0,0,180))			dxDrawRectangle(posx - (12 + (0.5 * width)),posy - (0 * 20) + 1,width + 23,13,tocolor(0,0,0,0))			dxDrawRectangle(posx - (12 + (0.5 * width)),posy - (0 * 20) + 4,width + 23,7,tocolor(0,0,0,180))			local r,g,b = 255, 255, 255			dxDrawText(text,posx - (0.5 * width),posy - (0 * 20),posx - (0.5 * width),posy - (0 * 20),tocolor(r,g,b,255),1,"default","left","top",false,false,false)		end	endendfunction isPedAiming ( thePedToCheck )	if isElement(thePedToCheck) then		if getElementType(thePedToCheck) == "player" or getElementType(thePedToCheck) == "ped" then			if getPedTask(thePedToCheck, "secondary", 0) == "TASK_SIMPLE_USE_GUN" then				return true			end		end	end	return falseendfunction createObjectXD(id,x,y,z,r1,r2,r3)	ob = createObject(id,x+50,y-1280,z-70,r1,r2,r3)endelectronic = {	createObjectXD(14665