ext, x, y, w, h, color, scale, font, alignX, alignY, clip, wordBreak, postGUI )endtick = 0addEventHandler("onClientRender",getRootElement(),function ()	for i,v in ipairs(getElementsByType("marker",resourceRoot)) do		if getElementDimension(localPlayer) == 0 then			local name = getElementData(v,"shopName")			if name then				local x,y,z = getElementPosition(v)				local x2,y2,z2 = getElementPosition(localPlayer)				local cx,cy,cz = getCameraMatrix()				if getDistanceBetweenPoints3D(cx,cy,cz,x,y,z) <= 25 then					local px,py = getScreenFromWorldPosition(x,y,z+1.3,0.06)					if px then						if z2 <= z+4 then							local width = dxGetTextWidth(name,1,"sans")							local r,g,b = getMarkerColor(v)--							tick = tick + 1							dxDrawBorderedText("Store Robbery\n"..name, px, py, px, py, tocolor(255, 255, 255, 255), 2, "sans", "center", "center", false, false)							if r == 255 then								setMarkerColor(v,0,100,200,150)							else								setMarkerColor(v,255,0,0,150)							end						end					end				end			end		end	endend)