function renderInterpolation()
	local now = getTickCount()
	local duration = 2000
	local elapsed = now - start
	local progress = (now - start) / (duration)
	-- pistol
	pistolPos = interpolateBetween(0, 0, 0, (skillsTab.pistol/1000)*503, 0, 0, progress, "Linear")
	pistolPercentage = interpolateBetween(0, 0, 0, (skillsTab.pistol/10), 0, 0, progress, "Linear")
	-- silenced
	silencedPos = interpolateBetween(0, 0, 0, (skillsTab.sil/1000)*503, 0, 0, progress, "Linear")
	silencedPercentage = interpolateBetween(0, 0, 0, (skillsTab.sil/10), 0, 0, progress, "Linear")
	-- deagle
	degPos = interpolateBetween(0, 0, 0, (skillsTab.deg/1000)*503, 0, 0, progress, "Linear")
	degPercentage = interpolateBetween(0, 0, 0, (skillsTab.deg/10), 0, 0, progress, "Linear")
	-- shotgun
	shotgunPos = interpolateBetween(0, 0, 0, (skillsTab.shot/1000)*503, 0, 0, progress, "Linear")
	shotgunPercentage = interpolateBetween(0, 0, 0, (skillsTab.shot/10), 0, 0, progress, "Linear")
	-- sawned
	sawnedPos = interpolateBetween(0, 0, 0, (skillsTab.sawned/1000)*503, 0, 0, progress, "Linear")
	sawnedPercentage = interpolateBetween(0, 0, 0, (skillsTab.sawned/10), 0, 0, progress, "Linear")
	-- spaz
	spazPos = interpolateBetween(0, 0, 0, (skillsTab.spaz/1000)*503, 0, 0, progress, "Linear")
	spazPercentage = interpolateBetween(0, 0, 0, (skillsTab.spaz/10), 0, 0, progress, "Linear")
	-- uzi
	uziPos = interpolateBetween(0, 0, 0, (skillsTab.uzi/1000)*503, 0, 0, progress, "Linear")
	uziPercentage = interpolateBetween(0, 0, 0, (skillsTab.uzi/10), 0, 0, progress, "Linear")
	-- mp5
	mp5Pos = interpolateBetween(0, 0, 0, (skillsTab.mp5/1000)*503, 0, 0, progress, "Linear")
	mp5Percentage = interpolateBetween(0, 0, 0, (skillsTab.mp5/10), 0, 0, progress, "Linear")
	-- ak47
	akPos = interpolateBetween(0, 0, 0, (skillsTab.ak/1000)*503, 0, 0, progress, "Linear")
	akPercentage = interpolateBetween(0, 0, 0, (skillsTab.ak/10), 0, 0, progress, "Linear")
	-- m4
	mPos = interpolateBetween(0, 0, 0, (skillsTab.m4/1000)*503, 0, 0, progress, "Linear")
	mPercentage = interpolateBetween(0, 0, 0, (skillsTab.m4/10), 0, 0, progress, "Linear")
	-- sniper
	sniperPos = interpolateBetween(0, 0, 0, (skillsTab.sniper/1000)*503, 0, 0, progress, "Linear")
	sniperPercentage = interpolateBetween(0, 0, 0, (skillsTab.sniper/10), 0, 0, progress, "Linear")
	if (now > start + duration) then
		removeEventHandler("onClientRender", root, renderInterpolation)
	end
end 

function drawSkillsWnd(newTab)
	if not (isSkillsWndShowing) then
		skillsTab = newTab
		start = getTickCount()
		addEventHandler("onClientRender", root, renderInterpolation)
		addEventHandler("onClientRender", root, initSkillWnd)
		isSkillsWndShowing = true
	else
		removeEventHandler("onClientRender", root, renderInterpolation)
		removeEventHandler("onClientRender", root, initSkillWnd)
		isSkillsWndShowing = false
	end
	showCursor(isSkillsWndShowing)
end
addEvent("AURskills.drawSkillsWnd", true)
addEventHandler("AURskills.drawSkillsWnd", root, drawSkillsWnd)

function closeSkillsWindow(button, state, absoluteX, absoluteY)
	if (isSkillsWndShowing) and (state == "down") then 
		if ((absoluteX >= sX*614) and (absoluteX <= sX*(614+139)) and (absoluteY >= sY*631) and (absoluteY <= sY*(631+43))) then
			triggerEvent("AURskills.drawSkillsWnd", localPlayer)
		end
	end
end
addEventHandler("onClientClick", root, closeSkillsWindow)